mapper.select:
select * from admin 

	 and store_id =0 
	 and building_id =#{buildingId}

select count(id) from admin 

	 and level =#{level} 
	 and  name =#{name}  or account =#{account} 

	and buildingId =#{buildingId} and level =#{level} 
		and  name =#{name} or account =#{account}

mapper.update:
update admin set
pwd =#{pwd} where id=#{id} 
  and building_id =#{buildingId}

update admin  set name=#{name} 

and building_id=#{buildingId}
 and store_id#{id}
